//     header.classList.toggle("sticky",window.scrollY)
// })

const mobile_nav = document.querySelector(".mob-nav-btn");
const mob_nav_menu = document.querySelector(".mob-nav-menu");
const navBG = document.querySelector(".header")


mobile_nav.addEventListener("click", () => {
    const classListToToggle = [".logo", ".fj-container", ".scroll-pro"]; 
    
    mob_nav_menu.classList.toggle("active");
    document.body.classList.toggle("mobile-menu-active");

    if (navBG.classList.contains("sticky")) {
        navBG.classList.remove("sticky");
      }else{
        navBG.classList.add("sticky")
      }

    classListToToggle.forEach((className) => {
        const elements = document.querySelectorAll(`${className}`);
        elements.forEach((element) => {
            element.classList.toggle("mob-on");
        });
    });
});


/*-----------------------------Navbar Color change-----------------------------------*/



document.addEventListener("DOMContentLoaded", function () {
    // Function to set the initial styles
    function setInitialStyles() {
        const title = document.title;
        const logo = document.querySelectorAll(".nav-img .logo path");
        const navLinks = document.querySelectorAll(".nav-links .nav-link");
        const mobNavIcon = document.querySelector(".mob-nav-icon");
        const fjLogo = document.querySelector(".tofj a");
        const fjArrow = document.querySelector(".tofj svg path");

        if (title === "Felicette | Home" && window.scrollY === 0) {
            // Initial styles for the home page
            fjLogo.style.color="white";
            fjArrow.style.fill="white";
            mobNavIcon.style.stroke = "white";
            logo.forEach((element) => {
                element.style.fill = "white";
            });
            navLinks.forEach((element) => {
                element.style.color = "white";
                element.addEventListener("mouseenter", handleMouseEnter);
                element.addEventListener("mouseleave", handleMouseLeave);
            });
        }
        else {
            // Initial styles for other pages
            fjLogo.style.color="#85C3C4";
            fjArrow.style.fill="#85C3C4";
            logo.forEach((element) => {
                element.style.fill = "#85C3C4";
            });
            mobNavIcon.style.stroke = "#85C3C4";

            navLinks.forEach((element) => {
                element.style.color = "#85C3C4";
                element.addEventListener("mouseenter", handleMouseEnter);
                element.addEventListener("mouseleave", handleMouseLeave);
            });
        }
    }

    // Function to handle mouseenter
    function handleMouseEnter(event) {
        event.target.style.color = "#EEC825";
    }

    // Function to handle mouseleave
    function handleMouseLeave(event) {
        const title = document.title;
        if (title === "Felicette | Home" && window.scrollY === 0) {
            event.target.style.color = "white";
        } else if (title === "Felicette | Home") {
            event.target.style.color = "#85C3C4";
        } else {
            event.target.style.color = "#85C3C4"; // For other pages
        }
    }

    // Function to handle scroll styles
    function handleScrollStyles() {
        const title = document.title;
        const logo = document.querySelectorAll(".nav-img .logo path");
        const navLinks = document.querySelectorAll(".nav-links .nav-link");
        const mobNavIcon = document.querySelector(".mob-nav-icon");
        const fjLogo = document.querySelector(".tofj a");
        const fjArrow = document.querySelector(".tofj svg path");


        if (title === "Felicette | Home" && window.scrollY === 0) {
            fjLogo.style.color="white";
            fjArrow.style.fill="white";
            logo.forEach((element) => {
                element.style.fill = "white";
            });
            navLinks.forEach((element) => {
                element.style.color = "white";
            });
            mobNavIcon.style.stroke = "white";
        }else {
            fjLogo.style.color="#85C3C4";
            fjArrow.style.fill="#85C3C4";
            logo.forEach((element) => {
                element.style.fill = "#85C3C4"; // For other pages
            });
            navLinks.forEach((element) => {
                element.style.color = "#85C3C4"; // For other pages
            });
            mobNavIcon.style.stroke = "#85C3C4";
        }
    }

    setInitialStyles();

    window.addEventListener("scroll", handleScrollStyles);
});



