/*-----------------------------Navbar hide effect-----------------------------------*/



document.addEventListener("DOMContentLoaded", function () {
    // Function to handle the "sticky" class based on scroll position
    function handleStickyHeader() {
      var header = document.querySelector("header");
      if (document.title == "Feli 'N' Bro | Home") {
        header.classList.toggle("sticky", window.scrollY > 0);
      } else {
        header.classList.add("sticky");
      }
    }
  
    // Call the function when the page loads
    handleStickyHeader();
  
    // Add a scroll event listener to continuously update the "sticky" class
    window.addEventListener("scroll", handleStickyHeader);
  });



const mobile_nav = document.querySelector(".mob-nav-btn");
const mob_nav_menu = document.querySelector(".mob-nav-menu");
const toggleNavbar = () => {
  mob_nav_menu.classList.toggle("active");
  document.body.classList.toggle("mobile-menu-active");
};
mobile_nav.addEventListener("click", () => toggleNavbar());


